mapper.select:
SELECT USER_ID
FROM user u
    JOIN mail_domain as d
    ON d.DOMAIN_CD = u.BELONG
        AND u.user_id != #{email}
        AND u.belong = (
            SELECT belong
            FROM user u1
            WHERE u1.user_id = #{email}
        )
ORDER BY u.CNT_RECEIVED
    LIMIT 1;

mapper.update:
UPDATE bottle_letter
SET IS_REPORTED_MSG = 1
WHERE LETTER_ID = #{letter_id}
